import React, { Component } from "react";
import { Form, Container, Row, Col, Button } from "react-bootstrap";
import { Link, Route, withRouter, Switch } from "react-router-dom";
import "./Register.css";
import BasicNavbar from "./Components/basic-navbar";
import axios from 'axios';
// import User from './main/java/com/nimps/rpisystem/rpisystem'
class Register extends Component{

    url = 'localhost:8080/api/users';

    constructor(props) {
        super(props);
        this.state = {first_name:"", last_name:"", email:"", password:"", pantherId:"", isLoading: true};
        this.first_name = React.createRef();
        this.last_name = React.createRef();
        this.email = React.createRef();
        this.password = React.createRef();
        this.pantherId = React.createRef();

    }

    handleChangeFirstName(event){ this.setState({first_name: event.target.first_name}); }
    handleChangeLastName(event){ this.setState({last_name: event.target.last_name}); }
    handleChangeEmail(event){ this.setState({email: event.target.email}); }
    handleChangePassword(event){ this.setState({password: event.target.password}); }
    handleChangePantherId(event){ this.setState({pantherId: event.target.pantherId}); }

    async submitUser() {
        this.setState({isLoading: true});

        axios.post('/api/users',{
            first_name: this.state.first_name,
            last_name: this.state.last_name,
            email: this.state.email,
            password: this.state.password,
            pantherId: this.state.pantherId
        })
        .then(function (response) {
            console.log(response);
        })
        .catch(function (error) {
        console.log(error);
        });
    }

    render(){
        return(
            <div>
                <BasicNavbar></BasicNavbar>
                <Container className="Login">
                    <Row>
                        <Col xs={2} className="left-sidebar"></Col>
                        <Col className="main">
                            <h1>Register</h1>
                            <Form>
                                <Form.Group>
                                    <Form.Row>
                                        <Col>
                                            <Form.Label style={{fontSize:"20px"}}>First Name</Form.Label>
                                            <Form.Control value={this.state.first_name} onChange={this.handleChangeFirstName} autoFocus style={{fontSize:"15px"}} placeholder="First Name"></Form.Control>
                                        </Col>                                    
                                        <Col>
                                            <Form.Label style={{fontSize:"20px"}}>Last Name</Form.Label>
                                            <Form.Control value={this.state.last_name} onChange={this.handleChangeLastName} style={{fontSize:"15px"}} placeholder="Last Name"></Form.Control>
                                        </Col>
                                    </Form.Row>
                                </Form.Group>
                                <Form.Group controlId="formGroupEmail">
                                    <Form.Label style={{fontSize:"20px"}}>Email address</Form.Label>
                                    <Form.Control value={this.state.email} onChange={this.handleChangeEmail} style={{fontSize:"15px"}} type="email" placeholder="Enter email" />
                                </Form.Group>
                                <Form.Group controlId="formGroupPassword">
                                    <Form.Label style={{fontSize:"20px"}}>Password</Form.Label>
                                    <Form.Control value={this.state.password} onChange={this.handleChangePassword} style={{fontSize:"15px"}} type="password" placeholder="Password" />
                                </Form.Group>
                                {/* <Form.Group controlId="formGroupPassword">
                                    <Form.Label style={{fontSize:"20px"}}>Re-enter Password</Form.Label>
                                    <Form.Control style={{fontSize:"15px"}} type="password" placeholder="Re-Password" />
                                </Form.Group> */}
                                <Form.Group>
                                    <Form.Label style={{fontSize:"20px"}}>PantherID</Form.Label>
                                    <Form.Control value={this.state.pantherId} onChange={this.handleChangePantherId} style={{fontSize:"15px"}} placeholder="Ex: 020258617"></Form.Control>
                                </Form.Group>
                                <Link className="loginLink" to="/login">Already have an account? Click here to login</Link>
                            </Form>
                            {/* <Link className="registerLink" to="/register">Don't have an account? Click here to register</Link> */}
                            <div className="text-center" style={{paddingTop:"10px"}}>
                                <Button onClick={this.submitUser} style={{fontSize:"15px"}} variant="primary">Register</Button>
                            </div>
                        </Col>
                        <Col xs={2} className="right-sidebar"></Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

export default Register;
