    return(
        <div>
            <BasicNavbar />
            <Container>
                <Row>
                    <Form className="checkout-form">
                        <Form.Row>
                            <h1>Checkout Portion</h1>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col}>
                                <Form.Label>Student PantherId</Form.Label>
                                <Form.Control placeholder="Student's PantherId"></Form.Control>
                            </Form.Group>
                            <Form.Group as={Col}>
                                <Form.Label>Equipment Kit Barcode</Form.Label>
                                <Form.Control placeholder="Kit Barcode"></Form.Control>
                            </Form.Group>
                        </Form.Row>
                        <Button onClick={checkout} variant="primary">Checkout</Button>
                    </Form>
                </Row>
                <hr></hr>
                <Row>
                    <Form>
                        <Form.Row>
                            <h1>Checkin Portion</h1>
                        </Form.Row>
                        <Form.Row>
                            <Form.Group as={Col}>
                                <Form.Label>Student PantherId</Form.Label>
                                <Form.Control placeholder="Student's PantherId"></Form.Control>
