                            <Form.Group as={Col}>
                                <Form.Label>Equipment Kit Barcode</Form.Label>
                                <Form.Control placeholder="Kit Barcode"></Form.Control>
                            </Form.Group>
                        </Form.Row>
                    </Form>
                </Row>
            </Container>
        </div>
    );
}

